configuration.encoder:
// Encode downlink data from incoming Rule Engine message

// msg - JSON message payload downlink message json
// msgType - type of message, for ex. 'ATTRIBUTES_UPDATED', 'POST_TELEMETRY_REQUEST', etc.
// metadata - list of key-value pairs with additional data about the message
// integrationMetadata - list of key-value pairs with additional data defined in Integration executing this converter

/** Encoder **/

var data = {
    port: 100,
    confirmed: false,
    payload_fields: {
        tempHigh: msg.tempHigh,
        tempLow: msg.tempLow,
        RHHigh: msg.RHHigh,
        RHLow: msg.RHLow,
        tempThresholdsEnabled: msg.tempThresholdsEnabled,
        RHThresholdsEnabled: msg.RHThresholdsEnabled,
        tempRHActiveSample: msg.tempRHActiveSample,
        tempRHIdleSample: msg.tempRHIdleSample,
        secondsPerTick: 60,
        periodicReport:msg.periodicReport
    }
};

var result = {
    contentType: "JSON",
    data: JSON.stringify(data),
    metadata: {
        devId: metadata.devId
    }
};

return result;
