configuration.encoder:
// Encode downlink data from incoming Rule Engine message

// msg - JSON message payload downlink message json
// msgType - type of message, for ex. 'ATTRIBUTES_UPDATED', 'POST_TELEMETRY_REQUEST', etc.
// metadata - list of key-value pairs with additional data about the message
// integrationMetadata - list of key-value pairs with additional data defined in Integration executing this converter

/** Encoder **/

var data = {};
var decoded_payload = {};

var downlinks = [];
// Process data from incoming message and metadata

decoded_payload.sensorPosition = msg.sensorPosition;
decoded_payload.schedulingCycle = msg.schedulingCycle;

var application_ids = {
    application_id: "polysense"
};

var end_device_ids = {
    device_id:metadata.devId,
    application_ids: application_ids
};

downlinks[0] = {
    f_port: 251,
    decoded_payload:decoded_payload,
    priority: "NORMAL",
    confirmed: false
};

data = {
//    end_device_ids: end_device_ids,
    downlinks: downlinks
};

var result = {

    // downlink data content type: JSON, TEXT or BINARY (base64 format)
    contentType: "JSON",
    data: JSON.stringify(data),

    // Optional metadata object presented in key/value format
    metadata: {
            topic: "v3/polysense/devices/" + metadata.devId + "/down/replace"
    }

};

return result;
