var data = decodeToJson(payload);
var deviceName = data.end_device_ids.device_id;
var deviceType = 'air-pressure';

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    telemetry: {
         temperature: data.uplink_message.decoded_payload.DecodeDataObj.environment.temperature,
         gasResistance:data.uplink_message.decoded_payload.DecodeDataObj.environment.gasResistance,
         battery_voltage:data.uplink_message.decoded_payload.DecodeDataObj.battery_voltage,
         airPressure:data.uplink_message.decoded_payload.DecodeDataObj.environment.airPressure,
         humidity:data.uplink_message.decoded_payload.DecodeDataObj.environment.humidity
    }
};

function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    var str = decodeToString(payload);
    var data = JSON.parse(str);
    return data;
}

return result;
