var data = decodeToJson(payload);
var deviceName = data.end_device_ids.device_id;
var deviceType = data.end_device_ids.application_ids.application_id;

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    telemetry: {
         battery: data.uplink_message.decoded_payload.battery,
         freq_weight: data.uplink_message.decoded_payload.freq_weight,
         la: data.uplink_message.decoded_payload.la,
         laeq: data.uplink_message.decoded_payload.laeq,
         lamax: data.uplink_message.decoded_payload.lamax,
         time_weight: data.uplink_message.decoded_payload.time_weight
 
    }
};

function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    var str = decodeToString(payload);
    var data = JSON.parse(str);
    return data;
}

return result;
