self.onInit = function() {

    $scope = self.ctx.$scope;


    $scope.onButtonClick = function($event) {
        if ($event) {
            $event.stopPropagation();
        }
        var descriptors = self.ctx.actionsApi.getActionDescriptors('rowClick');

        self.ctx.actionsApi.handleWidgetAction($event, descriptors[0]);
    }
}

self.onDestroy = function() {

}

self.actionSources = function() {
    return {
        'rowClick': {
            name: 'widget-action.row-click',
            multiple: false
        }
    };
}

